function process(input, output) {
	var filter = [[1,0,-1],[2,0,-2],[1,0,-1]];
    var x, y, xx, yy;
    for (y=1; y<output.height-1; y++) {
        for (x=1; x<output.width-1; x++) {
        	output[y][x].r = 0;
            output[y][x].g = 0;
            output[y][x].b = 0;

            for (yy=-1; yy<=1; yy++) {
            	for (xx=-1; xx<=1; xx++) {
            		output[y][x].r += filter[yy+1][xx+1] * input[y+yy][x+xx].r;
            		output[y][x].g += filter[yy+1][xx+1] * input[y+yy][x+xx].g;
            		output[y][x].b += filter[yy+1][xx+1] * input[y+yy][x+xx].b;
            	}
            }
        }
    }
}
