function process(input, output) {
    var x, y, rad = 5;
    for (y=0; y<output.height; y++) {
        for (x=0; x<output.width; x++) {
            var dx = Math.round(Math.random()*rad);
            var dy = Math.round(Math.random()*rad);

            var xx = x+dx;
            if (xx<0) xx = 0;
            if (xx>=input.width) xx = input.width - 1;

            var yy = y+dy;
            if (yy<0) yy = 0;
            if (yy>=input.height) yy = input.height-1;

            output[y][x].r = input[yy][xx].r;
            output[y][x].g = input[yy][xx].g;
            output[y][x].b = input[yy][xx].b;
        }
    }
}
