function process(input, output) {
    var x, y, f1, f2;
    for (y=0; y<output.height-2; y++) {
        for (x=0; x<output.width-2; x++) {
            f1 = input[y][x].g + input[y+1][x].g + input[y+2][x].g
            - 2*(input[y][x+1].g + input[y+1][x+1].g + input[y+2][x+1].g)
            + input[y][x+2].g + input[y+1][x+2].g + input[y+2][x+2].g;

            f2 = input[y][x].g + input[y][x+1].g + input[y][x+2].g
            - 2*(input[y+1][x+1].g + input[y+1][x+1].g + input[y+1][x+2].g)
            + input[y+2][x].g + input[y+2][x+1].g + input[y+2][x+2].g;
            if( (f1 > 0.3) || (f2 > 0.3) ) f=1;
            else f=0;
            output[y][x].r = f;
            output[y][x].g = f;
            output[y][x].b = input[y][x].g;
        }
    }
}
