function process(input, output) {
    var x, y;
    for (y=0; y<output.height; y++) {
        for (x=0; x<output.width; x++) {
            output[y][x].r = quant(input[y][x].r);
            output[y][x].g = quant(input[y][x].g);
            output[y][x].b = quant(input[y][x].b);
        }
    }
}

function quant(val) {
    if (val<0.25) return 0.00;
    if (val<0.5 ) return 0.25;
    if (val<0.75) return 0.50;
    return 1.00;
}
