function process(input, output) {
    var x, y;
    for (y=0; y<output.height; y++) {
        for (x=0; x<output.width; x++) {
            output[y][x].r = solar(input[y][x].r);
            output[y][x].g = solar(input[y][x].g);
            output[y][x].b = solar(input[y][x].b);
        }
    }
}

function solar(val) {
    if (val<0.5) return 1-val;
    return val;
}
