function process(input, output) {
    var x, y;
    for (y=0; y<output.height; y++) {
        for (x=0; x<output.width; x++) {
            warhol(input[y][x], output[y][x]);
        }
    }
}

function warhol(inp, out) {
    var grey = (inp.r + inp.g + inp.b) / 3;

    if (grey<0.2) {
        out.r = 0; out.g = 0; out.b = 1;
    } else if (grey<0.4) {
        out.r = 1; out.g = 0; out.b = 1;
    } else if (grey<0.6) {
        out.r = 0; out.g = 1; out.b = 1;
    } else if (grey<0.8) {
        out.r = 1; out.g = 1; out.b = 0;
    } else {
        out.r = 1; out.g = 0; out.b = 0;
    }
}
